def wp_range(m,x):
  n = 0
  r = 10 + int(x / 10)
  n |= wp_one_handed(m)
  n |= wp_two_handed(m)
  n |= wp_polearm(m)
  n |= wp_archery(x + 20)
  n |= wp_crossbow(x +10)
  n |= wp_throwing(x)
  n |= wp_firearm(x +10)
  return n
